import React, { useEffect, useState } from "react";
import Card from "../Components/Card";
import Navbar from "../Components/Navbar";
import CustomSpinner from "../Components/CustomSpinner";

// Image Imports
import defaultProfile from "../Assets/profile.png";
import userProfile1 from "../Assets/profile1.png";
import userProfile4 from "../Assets/profile4.jpeg";
import userProfile5 from "../Assets/profile5.jpeg";
import userProfile6 from "../Assets/profile6.png";
import userProfile7 from "../Assets/profile7.png";
import { API_URL } from "../Config";

const Dashboard = () => {
  // State Variables
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusData, setStatusData] = useState({});
  const [userGroups, setUserGroups] = useState({});
  const [priorityGroups, setPriorityGroups] = useState({});
  const [groupingCriteria, setGroupingCriteria] = useState('status');
  const [orderingCriteria, setOrderingCriteria] = useState('priority');
  const [onlineUsers, setOnlineUsers] = useState({});
  const [ticketStatusMap, setTicketStatusMap] = useState({});
  
  const statusLabels = ["Backlog", "Todo", "In progress", "Done", "Canceled"];
  
  // Fetch Data
  useEffect(() => {
    fetchData();
  }, [groupingCriteria, orderingCriteria]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch(API_URL);
      const data = await response.json();
      setTickets(data.tickets);
      setOnlineUsers(mapUserAvailability(data.users));
      setStatusData(groupByStatus(data.tickets));
      setUserGroups(groupByUser(data.tickets));
      setPriorityGroups(groupByPriority(data.tickets));
      setTicketStatusMap(mapStatus(data));
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  const mapUserAvailability = (users) => 
    users.reduce((acc, user) => ({ ...acc, [user.id]: user.available }), {});

  const mapStatus = (data) => {
    const mapping = {};
    data.tickets.forEach(ticket => {
      mapping[ticket.id] = ticket.status;
    });
    return mapping;
  };

  const groupByStatus = (tickets) => {
    const grouped = tickets.reduce((acc, ticket) => {
      if (!acc[ticket.status]) acc[ticket.status] = [];
      acc[ticket.status].push(ticket);
      return acc;
    }, {});
    
    statusLabels.forEach(key => {
      if (!grouped[key]) grouped[key] = [];
    });
    
    return {
      Keys: statusLabels,
      ...grouped,
    };
  };

  const groupByUser = (tickets) => {
    const grouped = tickets.reduce((acc, ticket) => {
      if (!acc[ticket.userId]) acc[ticket.userId] = [];
      acc[ticket.userId].push(ticket);
      return acc;
    }, {});
    
    return {
      Keys: Object.keys(grouped),
      ...grouped,
    };
  };

  const groupByPriority = (tickets) => {
    const grouped = tickets.reduce((acc, ticket) => {
      if (!acc[ticket.priority]) acc[ticket.priority] = [];
      acc[ticket.priority].push(ticket);
      return acc;
    }, {});
    
    return {
      Keys: Object.keys(grouped),
      ...grouped,
    };
  };

  const renderCards = (items) => (
    items.map(ticket => (
      <Card
        key={ticket.id}
        id={ticket.id}
        title={ticket.title}
        tag={ticket.tag}
        userId={ticket.userId}
        priority={ticket.priority}
        userData={onlineUsers}
        grouping={groupingCriteria}
        ordering={orderingCriteria}
        status={statusData}
        statusMapping={ticketStatusMap}
      />
    ))
  );

  return (
    <div>
      <Navbar
        grouping={groupingCriteria}
        setGrouping={setGroupingCriteria}
        ordering={orderingCriteria}
        setOrdering={setOrderingCriteria}
        call={fetchData}
      />
      <div className="Dashboard-Container">
        {loading ? (
          <CustomSpinner />
        ) : (
          <>
            {groupingCriteria === "status" && statusData.Keys.map((status, index) => (
              <div className="column" key={index}>
                <div className="Header">
                  <div className="icon-text">
                    <i className={`bx ${getStatusIcon(status)}`}></i>
                    <span className="text">{status === "In progress" ? "In Progress" : status}</span>
                    <span>{statusData[status]?.length}</span>
                  </div>
                  <div className="actions">
                    <i className="bx bx-plus" id="plus"></i>
                    <i className="bx bx-dots-horizontal-rounded" id="dots"></i>
                  </div>
                </div>
                {renderCards(statusData[status])}
              </div>
            ))}

            {groupingCriteria === "users" && userGroups.Keys.map((userId, index) => (
              <div className="column" key={index}>
                <div className="Header">
                  <div className="icon-text">
                    <div className={onlineUsers[userId] ? "user-avatar" : "user-avatar-unavailable"}>
                      <img
                        src={getProfileImage(userId)}
                        className="user-avatar"
                        alt="user"
                      />
                    </div>
                    <span className="text">{userGroups[userId]?.[0]?.userName || "Unknown"}</span>
                    <span>{userGroups[userId]?.length}</span>
                  </div>
                  <div className="actions">
                    <i className="bx bx-plus" id="plus"></i>
                    <i className="bx bx-dots-horizontal-rounded" id="dots"></i>
                  </div>
                </div>
                {renderCards(userGroups[userId])}
              </div>
            ))}

            {groupingCriteria === "priority" && priorityGroups.Keys.map((priority, index) => (
              <div className="column" key={index}>
                <div className="Header">
                  <div className="icon-text-priority">
                    <i className={`bx ${getPriorityIcon(priority)}`}></i>
                    <span className="text">{getPriorityLabel(priority)}</span>
                    <span className="count">{priorityGroups[priority]?.length}</span>
                  </div>
                  <div className="actions">
                    <i className="bx bx-plus" id="plus"></i>
                    <i className="bx bx-dots-horizontal-rounded" id="dots"></i>
                  </div>
                </div>
                {renderCards(priorityGroups[priority])}
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

// Helper Functions
const getStatusIcon = (status) => {
  switch (status) {
    case "Todo":
      return "bx-circle";
    case "In progress":
      return "bx-adjust";
    case "Backlog":
      return "bx-task-x";
    case "Done":
      return "bxs-check-circle";
    case "Canceled":
      return "bxs-x-circle";
    default:
      return "bx-circle";
  }
};

const getPriorityIcon = (priority) => {
  switch (priority) {
    case "0":
      return "bx-dots-horizontal-rounded";
    case "1":
      return "bx-signal-2";
    case "2":
      return "bx-signal-3";
    case "3":
      return "bx-signal-4";
    case "4":
      return "bxs-message-square-error";
    default:
      return "bx-dots-horizontal-rounded";
  }
};

const getPriorityLabel = (priority) => {
  switch (priority) {
    case "0":
      return "No Priority";
    case "1":
      return "Low";
    case "2":
      return "Medium";
    case "3":
      return "High";
    case "4":
      return "Urgent";
    default:
      return "No Priority";
  }
};

const getProfileImage = (userId) => {
  switch (userId) {
    case "usr-1":
      return userProfile1;
    case "usr-2":
      return userProfile6;
    case "usr-3":
      return userProfile7;
    case "usr-4":
      return userProfile4;
    default:
      return defaultProfile;
  }
};

export default Dashboard;
